(function ($) {

    $.fn.extend({
        showLoading: function () {
            $('#loading').remove();
            $(this).after($('<span id="loading"><img alt="" src="data:image/gif;base64," title="loading" style="width: 15px;padding-right:5px;" /> Loading....</span>').show('slow'));
        },
        hideLoading: function () {
            $('#loading').remove();
        }
    });


    $.showFullLoading = function (idLoading) {
        $('body').append(
                $('<div id="fullLoading" ' + (idLoading ? 'idLoading="' + idLoading + '"' : '') + ' style="display: block;" role="status" aria-hidden="false">' +
                    '<div class="ProgressHolder">' +
                        '<div class="divLoading">' +
                            '<div class="divLoadingImg">' +
                                '<img alt="Loading..." src="data:image/gif;base64," />' +
                            '</div>' +
                            '<div class="divLoadingText">' +
                                'Loading...' +
                            '</div>' +
                        '</div>' +
                    '</div>' +
                  '</div>'));
    };

    $.hideFullLoading = function (idLoading) {
        if (idLoading) {
            $('#fullLoading[idLoading="' + idLoading + '"]').remove();
        } else {
            $('#fullLoading').remove();
        }
    }
})(jQuery);